import React, { useState } from 'react';
import './Services.css';
import { Link } from 'react-router-dom';
import servicesBg from '../assets/Services-bg.jpg';

const servicesList = [
  "Film Production",
  "Indie Films (90M content)",
  "Web Series",
  "Replace Serials",
  "Short Films / Documentaries",
  "Theatre Production",
  "Ad Films / Music Videos",
];

const Services = () => {
  const [hoveredIndex, setHoveredIndex] = useState(null);

  return (
    <div
      className="services-container"
      style={{
        backgroundImage: `linear-gradient(rgba(26, 0, 0, 0.85), rgba(26, 0, 0, 0.85)), url(${servicesBg})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundAttachment: 'fixed',
      }}
    >
      <h1 className="services-title">Our Services</h1>

      <div className="stacked-card-center">
        <div className="stacked-horizontal-wrapper">
          {servicesList.map((service, index) => (
            <div
              key={index}
              className={`service-card ${
                hoveredIndex === index
                  ? 'hovered'
                  : hoveredIndex !== null
                  ? 'not-hovered'
                  : ''
              }`}
              style={{
                left: `${index * 80}px`,
                zIndex: servicesList.length - index,
              }}
              onMouseEnter={() => setHoveredIndex(index)}
              onMouseLeave={() => setHoveredIndex(null)}
            >
              <h2>{service}</h2>
            </div>
          ))}
        </div>
      </div>

      <div className="services-nav">
        <Link to="/home" className="nav-btn">Home</Link>
        <Link to="/about" className="nav-btn">About Us</Link>
        <Link to="/contact" className="nav-btn">Contact</Link>
      </div>
    </div>
  );
};

export default Services;
