import React, { useState, useEffect, useRef } from 'react';
import { motion } from 'framer-motion';
import './LandingPage.css';
import logo from '../assets/logo.png';

const LandingPage = () => {
  const [showIntro, setShowIntro] = useState(true);
  const [audioBlocked, setAudioBlocked] = useState(false);
  const audioRef = useRef(null);

  useEffect(() => {
    const audio = audioRef.current;
    audio.volume = 0.5;

    const playMusic = async () => {
      try {
        await audio.play();
        setAudioBlocked(false);
        console.log("Music autoplayed");
      } catch (err) {
        console.warn("Autoplay blocked:", err);
        setAudioBlocked(true);
      }
    };

    playMusic();

    const timer = setTimeout(() => {
      setShowIntro(false);
      audio.pause();
      audio.currentTime = 0;
    }, 5000);

    return () => {
      clearTimeout(timer);
      audio.pause();
    };
  }, []);

  const handleManualPlay = () => {
    audioRef.current.play();
    setAudioBlocked(false);
  };

  return (
    <div className="landing-container">
      <audio ref={audioRef} src="/intro.mp3" preload="auto" />

      {audioBlocked && (
        <button className="unmute-button" onClick={handleManualPlay}>
          ðŸ”Š Tap to Enable Sound
        </button>
      )}

      {showIntro ? (
        <motion.div
          className="logo-wrapper"
          initial={{ opacity: 0, scale: 0.5, rotate: -180 }}
          animate={{ opacity: 1, scale: 1, rotate: 0 }}
          transition={{ duration: 2.5 }}
        >
          <img src={logo} alt="SUPRA Logo" className="logo-animated" />
          <motion.h1
            className="studio-text"
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 2, duration: 1.5 }}
          >
            SUPRA STUDIOS
          </motion.h1>
        </motion.div>
      ) : (
        <div className="homepage">
          <h1>Welcome to SUPRA Studios</h1>
          <p>Where stories come to life.</p>
        </div>
      )}
    </div>
  );
};

export default LandingPage;
