import React, { useState, useEffect, useRef } from 'react';
import { motion } from 'framer-motion';
import './LandingPage.css';
import logo from '../assets/logo.png';

const LandingPage = () => {
  const [started, setStarted] = useState(false);
  const [showIntro, setShowIntro] = useState(true);
  const audioRef = useRef(null);

  const handleStart = () => {
    setStarted(true);
    audioRef.current.play().catch((err) => {
      console.warn('Autoplay blocked by browser:', err);
    });

    setTimeout(() => {
      setShowIntro(false);
      audioRef.current.pause();
      audioRef.current.currentTime = 0;
    }, 5000);
  };

  return (
    <div className="landing-container">
      <audio ref={audioRef} src="/intro.mp3" preload="auto" />

      {!started ? (
        <div className="start-screen">
          <h1>SUPRA STUDIOS</h1>
          <button onClick={handleStart} className="start-btn">Enter</button>
        </div>
      ) : showIntro ? (
        <motion.div
          className="logo-wrapper"
          initial={{ opacity: 0, scale: 0.5, rotate: -180 }}
          animate={{ opacity: 1, scale: 1, rotate: 0 }}
          transition={{ duration: 2.5 }}
        >
          <img src={logo} alt="SUPRA Logo" className="logo-animated" />
          <motion.h1
            className="studio-text"
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 2, duration: 1.5 }}
          >
            SUPRA STUDIOS
          </motion.h1>
        </motion.div>
      ) : (
        <div className="homepage">
          <h1>Welcome to SUPRA Studios</h1>
          <p>Where stories come to life.</p>
        </div>
      )}
    </div>
  );
};

export default LandingPage;
