import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import './LandingPage.css';
import logo from '../assets/logo.png';

const LandingPage = () => {
  const [started, setStarted] = useState(false);
  const [showIntro, setShowIntro] = useState(true);
  const [swiped, setSwiped] = useState(false);
  const audioRef = useRef(null);

  const handleStart = () => {
    setStarted(true);
    audioRef.current.play().catch((err) => {
      console.warn('Autoplay blocked by browser:', err);
    });

    setTimeout(() => {
      setShowIntro(false);
      audioRef.current.pause();
      audioRef.current.currentTime = 0;
    }, 5000);
  };

  const handleDragEnd = (event, info) => {
    if (info.offset.y < -150) {
      // Swiped up far enough
      setSwiped(true);
    }
  };

  return (
    <div className="landing-container">
      <audio ref={audioRef} src="/intro.mp3" preload="auto" />

      {!started ? (
        <div className="start-screen">
          <h1>SUPRA STUDIOS</h1>
          <button onClick={handleStart} className="start-btn">Enter</button>
        </div>
      ) : showIntro ? (
        <motion.div
          className="logo-wrapper"
          initial={{ opacity: 0, scale: 0.5, rotate: -180 }}
          animate={{ opacity: 1, scale: 1, rotate: 0 }}
          transition={{ duration: 2.5 }}
        >
          <img src={logo} alt="SUPRA Logo" className="logo-animated" />
          <motion.h1
            className="studio-text"
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 2, duration: 1.5 }}
          >
            SUPRA STUDIOS
          </motion.h1>
        </motion.div>
      ) : !swiped ? (
        <motion.div
          className="welcome-slide"
          drag="y"
          dragConstraints={{ top: 0, bottom: 0 }}
          onDragEnd={handleDragEnd}
          initial={{ y: 0 }}
          animate={{ y: 0 }}
        >
          <h1 className="welcome-heading">Welcome to SUPRA STUDIOS</h1>
          <p className="welcome-subtext">Swipe up to explore</p>
          <div className="swipe-indicator">⬆️</div>
        </motion.div>
      ) : (
        <div className="homepage">
          <h1>SUPRA Studios Homepage</h1>
          <p>This is where your main site content goes.</p>
          {/* You can add more sections here like About, Work, Contact */}
        </div>
      )}
    </div>
  );
};

export default LandingPage;
