import React, { useState, useEffect, useRef } from 'react';
import { motion } from 'framer-motion';
import './LandingPage.css';
import logo from '../assets/logo.png';

const LandingPage = () => {
  const [showIntro, setShowIntro] = useState(true);
  const audioRef = useRef(null);

  useEffect(() => {
    const audio = audioRef.current;
    audio.volume = 0.5;

    // Try to autoplay music on every page load
    const playAudio = () => {
      const playPromise = audio.play();
      if (playPromise !== undefined) {
        playPromise
          .then(() => {
            console.log("ðŸŽµ Music is playing");
          })
          .catch((error) => {
            console.warn("Autoplay blocked:", error);
          });
      }
    };

    playAudio();

    // Stop animation + music after 5 seconds
    const timer = setTimeout(() => {
      setShowIntro(false);
      audio.pause();
      audio.currentTime = 0;
    }, 5000);

    return () => {
      clearTimeout(timer);
      audio.pause();
    };
  }, []);

  return (
    <div className="landing-container">
      <audio ref={audioRef} src="/intro.mp3" preload="auto" />
      {showIntro ? (
        <motion.div
          className="logo-wrapper"
          initial={{ opacity: 0, scale: 0.5, rotate: -180 }}
          animate={{ opacity: 1, scale: 1, rotate: 0 }}
          transition={{ duration: 2.5 }}
        >
          <img src={logo} alt="SUPRA Logo" className="logo-animated" />
          <motion.h1
            className="studio-text"
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 2, duration: 1.5 }}
          >
            SUPRA STUDIOS
          </motion.h1>
        </motion.div>
      ) : (
        <div className="homepage">
          <h1>Welcome to SUPRA Studios</h1>
          <p>Where stories come to life.</p>
        </div>
      )}
    </div>
  );
};

export default LandingPage;
