import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import './LandingPage.css';
import logo from '../assets/logo.png';

const LandingPage = () => {
  const [showIntro, setShowIntro] = useState(true);

  // Automatically switch to homepage after 5 seconds
  useEffect(() => {
    const timer = setTimeout(() => {
      setShowIntro(false);
    }, 5000); // 5 seconds
    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="landing-container">
      {showIntro ? (
        <motion.div
          className="logo-wrapper"
          initial={{ opacity: 0, scale: 0.5, rotate: -180 }}
          animate={{ opacity: 1, scale: 1, rotate: 0 }}
          transition={{ duration: 2.5, ease: 'easeOut' }}
        >
          <img src={logo} alt="SUPRA Logo" className="logo-animated" />
          <motion.h1
            className="studio-text"
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 2, duration: 1.5 }}
          >
            SUPRA STUDIOS
          </motion.h1>
        </motion.div>
      ) : (
        <div className="homepage">
          <h1>Welcome to SUPRA STUDIOS</h1>
          <p>Where stories come to life.</p>
          {/* You can add more sections like About, Projects, Contact here */}
        </div>
      )}
    </div>
  );
};

export default LandingPage;
